CREATE TABLE Contact (id INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1, INCREMENT BY 1) PRIMARY KEY, firstName VARCHAR(100) NOT NULL, lastName VARCHAR(100), mobile VARCHAR(100),emailAddress VARCHAR(100));

CREATE TABLE Address (id INTEGER IDENTITY PRIMARY KEY, street VARCHAR(100), city VARCHAR(100), zip VARCHAR(100), country VARCHAR(100), contact_id INTEGER, FOREIGN KEY (contact_id) REFERENCES Contact(id));